mapper.select:
SELECT
	COUNT(*)
FROM 
	staff
WHERE
	id=#{staffId}
AND problem=#{problem}
AND answer=#{answer}

mapper.update:
UPDATE staff
SET
	password=#{password}
WHERE
	id=#{staffId}

UPDATE staff
SET
	partname=#{partment},
	phoneNumber=#{phoneNumber}
WHERE
	id=#{staffId}

mapper.select:
SELECT medicine.id as medicineId, medicine.name as medicineName, customer.username as customerName, count, packagecode, companyid, customerid FROM medicine,indent,customer
medicine.id=indent.medicineid AND indent.customerid=customer.id AND state='未发货'
AND indent.medicineid=#{medicineId}

mapper.update:
UPDATE medicine
SET
	amount=amount-#{num}
WHERE
	id=#{medicineId}

UPDATE medicine
SET
	amount=amount+#{quantity}
WHERE
	id=#{medicineId}

UPDATE customermess
SET
	staffid=#{staffId},
	answer=#{answer}
WHERE
	customerid=#{customerId}
